var NUM_IMAGE = 2;
var imagesValue = null;

/* Create table if not exist */
db.transaction(function(tx){
	tx.executeSql('SELECT COUNT(*) FROM numbersImages', [], null, function(tx, err){
		tx.executeSql('CREATE TABLE IF NOT EXISTS numbersImages (id unique, text)');
	});
	 /*tx.executeSql("INSERT INTO numbersImages (id, text) VALUES (1,'data:image/png;base64,%3D')");
	 tx.executeSql("INSERT INTO numbersImages (id, text) VALUES (2,'data:image/png;base64,%3D')");*/
}, function(err){
	console.log("[first] ops db : ", err.message);
});

/* Check how many row, if not good number, drop and reinsert good data */

db.transaction(function(tx){
	tx.executeSql('SELECT COUNT(*) AS numImage FROM numbersImages', [], function(tx, results){
		var numImage = results.rows.item(0).numImage;
		if(numImage != NUM_IMAGE){
			tx.executeSql('DELETE FROM numbersImages',[], null, function(tx,err){
				console.log("MEEEERDE : ", err.message);
			});

			$.ajax({
	            type: "GET",
	            url: "datas/images.xml",
	            dataType: "xml",
	            success: function(xml) {
	            	imagesValue = $(xml).find('src');
					db.transaction(function(tx){
						for(i=0; i<imagesValue.length; i++){
							tx.executeSql("INSERT INTO numbersImages (id, text) VALUES ("+i+",'"+$(imagesValue[i]).text()+"')");
						}
					}, function(err){console.log("[populate DB] ops :", err.message)});
	            }
	        });
		}
	});
});

function getImage(id){
	var image = new Image();
	db.transaction(function(tx){
		tx.executeSql("SELECT text FROM numbersImages WHERE ID="+id, [], function(tx, results){
			if(results.rows.length == 1){
				image.src = results.rows.item(0).text;
			}
		});
	}, function(err){
		console.log('[getImage] ops db: ', err.message);
	})
	return image;
}
